class JSZip {
    constructor() {
        this._files = new Map();
    }

    file(name, content) {
        this._files.set(name, content);
        return this;
    }

    async generateAsync({ type } = {}) {
        if (type === 'blob') {
            const parts = [];
            for (const [name, content] of this._files) {
                parts.push(`--- ${name} ---\n`);
                parts.push(content);
                parts.push('\n');
            }
            return new Blob(parts, { type: 'application/zip' });
        }
        throw new Error(`JSZip stub: unsupported type "${type}"`);
    }
}

export default JSZip;
